import React from 'react';

import CountUp from 'react-countup';

export default function Votes({ value }) {
  return (
    <div>
      <CountUp start={value - 1000} end={value} duration={0.6} separator=" ">
        {({ countUpRef, start }) => (
          <div>
            <span ref={countUpRef} />
            <button onClick={start}>Start</button>
          </div>
        )}
      </CountUp>
    </div>
  );
}
