import React from 'react';

import CountUp from 'react-countup';

export default function Percentage({ value, previous }) {
  return (
    <div>
      <CountUp
        start={value - previous || 0}
        end={value}
        duration={0.6}
        decimals={2}
        decimal=","
        suffix="%"
      >
        {({ countUpRef }) => (
          <div>
            <span ref={countUpRef} />
          </div>
        )}
      </CountUp>
    </div>
  );
}
