import React from 'react';
import Info from './Info';
import Name from './Name';
import Percentage from './Percentage';
import Picture from './Picture';
import Popularity from './Popularity';
import Position from './Position';
import Votes from './Votes';

import css from './candidate.module.css';
import { formatNumber, formatPercentage } from '../helpers/formatHelpers';

export default function Candidate({ candidate, position }) {
  const { name, votes, percentage, popularity, id, previousVotes } = candidate;
  const imageSource = `${id}.jpg`;
  return (
    <div className={css.flexRow}>
      <Position>{position}</Position>
      <Picture imageSrc={imageSource} description={name}></Picture>
      <Info>
        <Name>{name}</Name>
        <Votes value={votes} voteCount={previousVotes} />
        <Percentage>{formatPercentage(percentage)}</Percentage>
        <Popularity value={popularity}></Popularity>
      </Info>
    </div>
  );
}
