import React from 'react';
import Candidate from './Candidate';
import Card from './Card';
import FlipMove from 'react-flip-move';

export default function Candidates({ candidates, prevVotes, previousPercent }) {
  return (
    <div>
      <FlipMove>
        {candidates.map((candidate, index) => {
          const { id } = candidate;
          const previousVoteObject = prevVotes.find((item) => item.id === id);

          const previousPercentageObject = previousPercent.find(
            (item) => item.id === id
          );

          const previousVotes = !!previousVoteObject
            ? previousVoteObject.vote
            : 0;

          const previousPercentage = !!previousPercentageObject
            ? previousPercentageObject.vote
            : 0;
          return (
            <div key={id}>
              <Card>
                <Candidate
                  previousVote={previousVotes}
                  candidate={candidate}
                  position={index + 1}
                />
              </Card>
            </div>
          );
        })}
      </FlipMove>
    </div>
  );
}
